mapper.select:
SELECT

FROM dept a
WHERE a.id = #{deptParm}

mapper.update:
UPDATE dept SET
del_flag = 'N'
WHERE id = #{deptId}

UPDATE dept SET
DEPT_CODE = #{deptCode},
DEPT_NAME = #{deptName},
DEPT_TYPE = #{deptType},
P_DEPT = #{pDept},
P_DEPTS = #{pDepts},
PRIMARY_PERSON = #{primaryPerson}
DEPUTY_PERSON = #{deptPerson}
DEL_FLAG = #{delFlag}
REMARK = #{remark}
WHERE id = #{id}
